 Example opening a SPI driver instance in blocking mode: SPI_Handlespi;
SPI_ParamsspiParams;

SPI_Params_init(&spiParams);
spiParams.transferMode=SPI_MODE_BLOCKING;
spi=SPI_open(Board_SPI0,&spiParams);

if(spi==NULL){
//ErroropeningSPI
while(1);
}

 Example opening a SPI driver instance in callback mode: SPI_Handlespi;
SPI_ParamsspiParams;

SPI_Params_init(&spiParams);
spiParams.transferMode=SPI_MODE_CALLBACK;
spiParams.transferCallbackFxn=UserCallbackFxn;

spi=SPI_open(Board_SPI0,&spiParams);
if(spi==NULL){
//ErroropeningSPI
while(1);
}

